SEARCH=
LAST_COMMIT=$(git log -1 --pretty=%B)
if [[ "$LAST_COMMIT" == *"Automatically generated by python-semantic-release"* ]]; then
  echo "This is the commit made by semantic-release. Skip running the CI/CD"
  exit 0
elif [[ "$LAST_COMMIT" == *"doc: update CHANGELOG.md"* ]]; then
  echo "This is the automatic changelog update. Skip running the CI/CD"
  exit 0
fi

