class SimpleOperations:
    def __init__(self, a, b):
        self.a = a
        self.b = b

    def add(self):
        operation = self.a + self.b
        return operation
    
    def subtract(self):
        operation = self.a - self.b
        return operation
    
    def multiply(self):
        operation = self.a * self.b
        return operation
    
    def divide(self):
        operation = self.a / self.b
        return operation